async function getData(e,r){const n=await fetch(e+r);return await n.json()}window.addEventListener("DOMContentLoaded",e=>{getData("/api/absentee/",triggerUpdate.value).then(e=>{let r=e.mail_instructions,n=e.in_person_instructions;document.querySelector("#ab-mail-instructions .card-header > h4").innerText=e.in_person_title,document.querySelector("#ab-person-instructions .card-header > h4").innerText=e.mail_title,document.querySelector("#ab-mail-instructions .card-body > p").innerHTML=r,document.querySelector("#ab-person-instructions .card-body > p").innerHTML=n,document.querySelector("#deadline-mail-request .card-body > p").innerHTML=e.mail_request_deadline,document.querySelector("#deadline-ballot-submissions .card-body > p").innerHTML=e.ballot_delivery,document.querySelector("#ab-requirements .card-body > p").innerHTML=e.requirements,document.querySelector("a#learn-more").href=e.source,document.querySelector("a#learn-more").innerText="Learn more about voting in "+triggerUpdate.value})}),triggerUpdate.addEventListener("change",e=>{getData("/api/absentee/",e.target.value).then(r=>{let n=r.mail_instructions,t=r.in_person_instructions;document.querySelector("#ab-mail-instructions .card-body > p").innerHTML=n,document.querySelector("#ab-person-instructions .card-body > p").innerHTML=t,document.querySelector("#deadline-mail-request .card-body > p").innerHTML=r.mail_request_deadline,document.querySelector("#deadline-ballot-submissions .card-body > p").innerHTML=r.ballot_delivery,document.querySelector("#ab-requirements .card-body > p").innerHTML=r.requirements,document.querySelector("a#learn-more").href=r.source,document.querySelector("a#learn-more").innerText="Learn more about voting in "+e.target.value})});