async function getData(e,t){const r=await fetch(e+t);return await r.json()}window.addEventListener("DOMContentLoaded",e=>{getData("/api/id/",triggerUpdate.value).then(e=>{let t=e.at_polls,r=e.other_options,n=e.no_id;document.querySelector("#at-polls .card-body > p").innerHTML=t,document.querySelector("#at-polls h4").innerHTML="Voter ID requirements in "+triggerUpdate.value,document.querySelector("#other-options .card-body > p").innerHTML=r,document.querySelector("#no-id .card-body > p").innerHTML=n,document.getElementById("learn-more-id").href=e.source,document.getElementById("learn-more-id").innerText="Learn more about voter ID requirements in "+triggerUpdate.value,document.getElementById("last-updated-id").innerText=triggerUpdate.value+" voter ID info last updated: "+e.last_checked})}),triggerUpdate.addEventListener("change",e=>{getData("/api/id/",e.target.value).then(t=>{let r=t.at_polls,n=t.other_options,o=t.no_id;document.querySelector("#at-polls .card-body > p").innerHTML=r,document.querySelector("#at-polls h4").innerHTML="Voter ID requirements in "+e.target.value,document.querySelector("#other-options .card-body > p").innerHTML=n,document.querySelector("#no-id .card-body > p").innerHTML=o,document.getElementById("learn-more-id").href=t.source,document.getElementById("learn-more-id").innerText="Learn more about voter ID requirements in "+e.target.value,document.getElementById("last-updated-id").innerText=e.target.value+" voter ID info last updated: "+t.last_checked})});