async function getData(e,n){const t=await fetch(e+n);return await t.json()}window.addEventListener("DOMContentLoaded",e=>{getData("/api/registration/general/",triggerUpdate.value).then(e=>{document.querySelector("#reg-in-person .card-body > p").innerText=e.inPerson,document.querySelector("#reg-by-mail .card-body > p").innerText=e.byMail,document.querySelector("#reg-online .card-body > p").innerText=e.online,e.onlineRegistration.length>1?(document.getElementById("vote-online-btn").style.display="inline",document.getElementById("vote-online-btn").innerText="Register to vote online in "+triggerUpdate.value,document.getElementById("vote-online-btn").href=e.onlineRegistration):document.getElementById("vote-online-btn").style.display="none"})}),triggerUpdate.addEventListener("change",e=>{getData("/api/registration/general/",e.target.value).then(n=>{document.querySelector("#reg-in-person .card-body > p").innerText=n.inPerson,document.querySelector("#reg-by-mail .card-body > p").innerText=n.byMail,document.querySelector("#reg-online .card-body > p").innerText=n.online,n.onlineRegistration.length>1?(document.getElementById("vote-online-btn").style.display="inline",document.getElementById("vote-online-btn").innerText="Register to vote online in "+e.target.value,document.getElementById("vote-online-btn").href=n.onlineRegistration):document.getElementById("vote-online-btn").style.display="none"})});